import React, {Component} from 'react';
import LoginButton from "./controls/Buttons";
import Container from "@material-ui/core/Container";
import {Box} from "@material-ui/core";
import Card from "@material-ui/core/Card";
import TextField from "@material-ui/core/TextField";
import {makeStyles} from "@material-ui/core/styles";
import IconButton from '@material-ui/core/IconButton';
import PersonIcon from '@material-ui/icons/Person';
import clsx from "clsx";
import {Grade, Visibility, VisibilityOff} from "@material-ui/icons";
import {blue, lightBlue, green, red, yellow, purple, grey} from "@material-ui/core/colors";
import VpnKeyIcon from '@material-ui/icons/VpnKey';
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Link from "@material-ui/core/Link";
import {AnimatedRotation, AnimatedPosition} from 'react-declare-animate';
import PhoneIcon from '@material-ui/icons/Phone';
import TextFormatIcon from '@material-ui/icons/TextFormat';
import EmailIcon from '@material-ui/icons/Email';
import Animate from "react-smooth";
import GraphicsHandler from './handlers/GraphicsHandler';
import Redirect from "react-router-dom/Redirect";
import Center from 'react-center';
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";
import TextBox from "./controls/TextBox";
import posed from 'react-pose';
import {Button, CardBody, CardFooter, CardHeader, Col, Form, FormGroup, Row} from "reactstrap";
import {getMode} from "../routes";
import FilePicker from "../components/FilePicker";

const LoginBox = posed.div({
    visible: {
        top: '50%',
        transition: { type: 'spring', duration: 750 },
    },
    hidden: {
        top: '150%',
        transition: { type: 'spring', duration: 750 },
    },
});

let classes = {};

let useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    margin: {
        margin: theme.spacing(1),
    },
    textField: {
        flexBasis: 200,
    }
}));

const inClasses = {
    rootInput: {
        background: 'transparent'
    },
};

function StylePreparer(props) {

    classes = useStyles();

    return (<Box/>);
}

class AuthPage extends Component {

    mobileWidth = 700;

    constructor(props) {
        super(props);
        this.state = {
            width: window.innerWidth,
            height: window.innerHeight,
            loading: 0,
            signupForCardAngles: false,
            signupForFormItems: false,
            forecasts: [],
            resizeTrigger: false,
            showPassword: false,
            goHome: false
        };
        document.body.style.overflowX = 'hidden';
        document.body.style.overflowY = 'hidden';
        GraphicsHandler.instance = new GraphicsHandler();
        this.loadingViewRef = React.createRef();
        this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
        setTimeout(() => {
            this.setState({
                loading: 1
            });
        }, 1000);
    }

    componentDidMount() {
        this.updateWindowDimensions();
        window.addEventListener('resize', this.updateWindowDimensions);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.updateWindowDimensions);
    }

    updateWindowDimensions() {
        GraphicsHandler.instance.width = window.innerWidth / GraphicsHandler.instance.density;
        GraphicsHandler.instance.height = window.innerHeight / GraphicsHandler.instance.density;
        this.setState({
            width: window.innerWidth,
            height: window.innerHeight
        });
    }

    measureAuthCardHeight() {
        if (this.state.loading >= 3) {
            if (GraphicsHandler.instance.isLongMobileScreen()) {
                document.body.style.overflowY = 'hidden';
                this.forceScrollReset();
            } else
                document.body.style.overflowY = 'auto';
        }
        return GraphicsHandler.instance.isMobileScreen() ?
            this.state.signupForFormItems ?
                '17cm' :
                '10cm' :
            this.state.signupForFormItems ?
                GraphicsHandler.instance.dpToPx(400) :
                GraphicsHandler.instance.dpToPx(350);
    }

    measureAuthCardWidth() {
        return this.state.width < 700 ?
            '80%' :
            600;
    }

    render() {

        if (this.state.goHome) {
            return <Redirect to='/Admin'/>
        }

        return (
            <div style={{
                width: '100',
                height: '100vh'
            }}>
                <LoginBox style={{
                    width:  this.measureAuthCardWidth(),
                    height: this.measureAuthCardHeight(),
                    position: 'absolute',
                    left: '50%',
                    transform: 'translate(-50%, -50%)',
                }}
                pose={this.state.loading === 1 ? 'visible' : 'hidden'}>
                    <div style={{
                        width: '100%',
                        height: 'calc(100% - ' + (window.devicePixelRatio * 72) + 'px)',
                        borderRadius: 16,
                        backgroundColor: '#27293d',
                        boxShadow: '0 1px 20px 0px rgba(0, 0, 0, 0.1)'
                    }}>
                        <Row>
                            <Col md="6">
                                <Card>
                                    <CardBody>
                                        <Form>
                                            <Row>
                                                <Col>
                                                    <FormGroup>
                                                        <label style={{
                                                            width: '100%',
                                                            textAlign: getMode() === '/rtl' ? 'right' : 'left'
                                                        }}>{getMode() === '/rtl' ? "انتخاب فایل" : "Pick file"}</label>
                                                        <div
                                                            style={{
                                                                border: '1px #e14eca solid',
                                                                borderRadius: 8,
                                                                padding: 8
                                                            }}
                                                        >
                                                        </div>
                                                        <label style={{
                                                            width: '100%',
                                                            textAlign: getMode() === '/rtl' ? 'right' : 'left'
                                                        }}>{getMode() === '/rtl' ? "انتخاب فایل" : "Pick file"}</label>
                                                        <div
                                                            style={{
                                                                border: '1px #e14eca solid',
                                                                borderRadius: 8,
                                                                padding: 8
                                                            }}
                                                        >
                                                        </div>

                                                        <label style={{
                                                            width: '100%',
                                                            textAlign: getMode() === '/rtl' ? 'right' : 'left'
                                                        }}>{getMode() === '/rtl' ? "انتخاب فایل" : "Pick file"}</label>
                                                        <div
                                                            style={{
                                                                border: '1px #e14eca solid',
                                                                borderRadius: 8,
                                                                padding: 8
                                                            }}
                                                        >
                                                        </div>
                                                    </FormGroup>
                                                </Col>
                                            </Row>
                                        </Form>
                                    </CardBody>
                                </Card>
                            </Col>
                        </Row>
                    </div>
                    <Center>
                        <LoginButton
                            variant="contained"
                            color="primary"
                            onClick={() => {

                            }}
                            style={{
                                outline: 'none',
                                position: 'relative',
                                top: '-24px',
                                width: '100px',
                                height: '48px',
                                zIndex: '3',
                            }}>
                            {this.state.signupForFormItems ? 'Sign Up' : 'Sign In'}
                        </LoginButton>
                    </Center>
                </LoginBox>
            </div>
        );
    }
}

AuthPage.propTypes = {
    classes: PropTypes.object.isRequired
};

export default withStyles(inClasses)(AuthPage);
